body.script:
let ws;
let lastDigits = [];
const activeSymbol = "R_100"; // Can change to R_10, R_25, etc.

function startBot() {
  const token = document.getElementById("token").value.trim();
  const status = document.getElementById("status");

  if (!token) {
    alert("Please enter your Deriv API Token!");
    return;
  }

  ws = new WebSocket("wss://ws.binaryws.com/websockets/v3?app_id=1089");

  ws.onopen = () => {
    status.innerText = "ğŸ” Connecting to Deriv...";
    ws.send(JSON.stringify({ authorize: token }));
  };

  ws.onmessage = (msg) => {
    const data = JSON.parse(msg.data);

    if (data.msg_type === "authorize") {
      status.innerText = "âœ… Authorized. Subscribing to ticks...";
      ws.send(JSON.stringify({ ticks: activeSymbol, subscribe: 1 }));
    }

    if (data.msg_type === "tick") {
      const digit = data.tick.quote.toString().slice(-1);
      lastDigits.push(digit);
      if (lastDigits.length > 10) lastDigits.shift();
      detectPattern(digit);
    }

    if (data.msg_type === "buy") {
      status.innerText = `ğŸ¯ Trade Executed: ${data.buy.contract_type} (Barrier ${data.buy.barrier})`;
    }

    if (data.error) {
      status.innerText = `âŒ Error: ${data.error.message}`;
    }
  };
}

function detectPattern(digit) {
  const status = document.getElementById("status");
  const len = lastDigits.length;

  if (len >= 3) {
    const d1 = lastDigits[len - 1];
    const d2 = lastDigits[len - 2];
    const d3 = lastDigits[len - 3];

    if (d1 === d2 && d2 === d3) {
      const repeatDigit = parseInt(d1);
      if (repeatDigit >= 7) {
        sendTrade("DIGITUNDER", repeatDigit);
        status.innerText = `ğŸ“‰ Signal: Under ${repeatDigit} (Repeat 3 times)`;
      } else if (repeatDigit <= 2) {
        sendTrade("DIGITOVER", repeatDigit);
        status.innerText = `ğŸ“ˆ Signal: Over ${repeatDigit} (Repeat 3 times)`;
      } else {
        status.innerText = `âš ï¸ Pattern: ${d1} x3 â€” No trade (digit 3â€“6)`;
      }
    }
  }
}

function sendTrade(type, barrierDigit) {
  const proposal = {
    buy: 1,
    price: 0.35,
    parameters: {
      amount: 0.35,
      basis: "stake",
      contract_type: type,
      currency: "USD",
      duration: 1,
      duration_unit: "t",
      symbol: activeSymbol,
      barrier: barrierDigit
    }
  };

  ws.send(JSON.stringify(proposal));
}
